package com.abdurashidov.broadcastsimport android.content.BroadcastReceiverimport android.content.Contextimport android.content.Intentimport android.widget.Toastimport com.abdurashidov.broadcasts.utils.NetworkHelperclass MyBroadcastReceiver:BroadcastReceiver() {    override fun onReceive(context: Context?, intent: Intent?) {        val networkHelper=NetworkHelper(context!!)        if (networkHelper.isNetworkConnected()){            Toast.makeText(context, "Connected", Toast.LENGTH_SHORT).show()        }else{            Toast.makeText(context, "Disconnected", Toast.LENGTH_SHORT).show()        }    }}